import { useState } from 'react';
import ReactDOM from 'react-dom';
import { js2xml } from 'xml-js';
import "./App.css"; 
import logo from './logo_ict_aac.png';

function MyForm() {
  const [appName, setAppName] = useState("");
  const [customAppName, setCustomAppName] = useState("");
  const [location, setLocation] = useState("");
  const [customLocation, setCustomLocation] = useState("");
  const [description, setDescription] = useState("");
  const [media, setMedia] = useState([]);
  const [deviceType, setDeviceType] = useState("");
  const [deviceModel, setDeviceModel] = useState(""); 
  const [osType, setOsType] = useState("");
  const [browserType, setBrowserType] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    if (appName === "Ostalo" && customAppName === "") {
      alert("Molimo unesite ime aplikacije.");
      return;
    }

    if (location === "Ostalo" && customLocation === "") {
      alert("Molimo unesite gdje ste uočili grešku u aplikaciji.");
      return;
    }

    if (deviceType === "Web aplikacija" && !browserType) { 
      alert("Molimo unesite vrstu internetskog preglednika.");
      return;
    }

    if (email && !isValidEmail(email)) {
      alert("Unesite ispravnu e-adresu.");
      return;
    }

    if (!appName || !location || !description || !deviceType || !deviceModel || !osType) {
      alert("Molimo vas da popunite sva obavezna polja.");
      return;
    }

    const formData = {
      appName,
      customAppName: appName === "Ostalo" ? customAppName : null, 
      location,
      customLocation: location === "Ostalo" ? customLocation : null, 
      media: media.map(file => ({ name: file.name, size: file.size })),
      deviceType,
      deviceModel,
      osType,
      browserType,
      email
    };

    const xmlData = js2xml(formData, { compact: true, ignoreComment: true, spaces: 4 });

    const filename = 'formData.xml';
    const blob = new Blob([xmlData], { type: 'text/xml' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', filename);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);

    event.target.reset();
    setMedia([]); 

    window.location.reload();
  }

  // Pretvorba slike ili videozapisa u Base64 format
  const fileToBase64 = async (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result.split(',')[1]);
      reader.onerror = (error) => reject(error);
    });
  };

  // Dodavanje Base64 zapisa u XML
  const addMediaToXml = async (files) => {
    const mediaData = [];
    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      const base64Data = await fileToBase64(file);
      mediaData.push({ name: file.name, type: file.type, data: base64Data });
    }
    return mediaData;
  };

  const handleAppChange = (event) => {
    setAppName(event.target.value);
  };

  const handleCustomAppNameChange = (event) => { 
    setCustomAppName(event.target.value);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleCustomLocationChange = (event) => {
    setCustomLocation(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const MAX_VIDEO_SIZE_MB = 100;

  const handleMediaChange = (event) => {
    const selectedFiles = event.target.files;
    const filesArray = Array.from(selectedFiles);

    for (let i = 0; i < filesArray.length; i++) {
      const file = filesArray[i];
      const extension = file.name.split('.').pop().toLowerCase(); 

      const allowedExtensions = ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'mp4', 'avi', 'mov', 'wmv'];

      if (!allowedExtensions.includes(extension)) {
        alert(`Nedopuštena datoteka: ${file.name}. Molimo odaberite datoteke s ekstenzijama: jpg, jpeg, png, gif, mp4, avi, mov, wmv.`);
        return;
      }

      if (file.type.includes('video') && file.size > MAX_VIDEO_SIZE_MB * 1024 * 1024) {
        alert(`Veličina videa "${file.name}" prelazi maksimalno dopuštenih ${MAX_VIDEO_SIZE_MB} MB.`);
        return;
      }
    }

    if (filesArray.length + media.length > 5) {
      alert("Možete odabrati maksimalno 5 datoteka.");
      return;
    }

    setMedia(prevMedia => [...prevMedia, ...filesArray]); 
  };

  const handleRemoveMedia = (index) => {
    setMedia(prevMedia => prevMedia.filter((_, i) => i !== index)); 
  };

  const handleDeviceTypeChange = (event) => {
    setDeviceType(event.target.value);
  };

  const handleDeviceModelChange = (event) => {
    setDeviceModel(event.target.value);
  };

  const handleOsTypeChange = (event) => {
    setOsType(event.target.value);
  };

  const handleBrowserTypeChange = (event) => {
    setBrowserType(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const isValidEmail = (email) => {
    const emailRegex = /[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/i;
    return emailRegex.test(email);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="logo-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="form-title">Prijava grešaka na ICT-AAC aplikacijama</h1>
      </div>
      <br /><br /> 
      <label>Ime aplikacije u kojoj ste uočili grešku: <span className="required">*</span></label>
      <select value={appName} onChange={handleAppChange} required>
        <option value="">Odaberite...</option>
        <option value="ciribuCiriglas">Ćiribu Ćiriglas</option>
        <option value="cujSvemir">Čuj SVEmir!</option>
        <option value="dominoBrojalica">Domino brojalica</option>
        <option value="endoUcilica">Endo-učilica</option>
        <option value="glaskalica">Glaskalica</option>
        <option value="gibalica">Gibalica</option>
        <option value="hakomKviz">HAKOM Kviz</option>
        <option value="jezicnaGradilica">Jezična gradilica</option>
        <option value="jezicnoGovornaVjezbalica">Jezično-govorna vježbalica</option>
        <option value="kolikoJeSati">Koliko je sati</option>
        <option value="komunikacijskiKljucevi">Komunikacijski ključevi</option>
        <option value="komunikator">Komunikator</option>
        <option value="komunikator+">Komunikator+</option>
        <option value="kuharica">KuhARica</option>
        <option value="kuna">Kuna</option>
        <option value="lozinka">Lozinka</option>
        <option value="malaGlaskalica">Mala Glaskalica</option>
        <option value="matematickaIgraonica">Matematička igraonica</option>
        <option value="matematickiVrtuljak">Matematički vrtuljak</option>
        <option value="matematika">Matematika</option>
        <option value="pamtilica">Pamtilica</option>
        <option value="pisalica">Pisalica</option>
        <option value="ponasalica">Ponašalica</option>
        <option value="pricajmoSlikama">Pričajmo slikama</option>
        <option value="pristupacniWeb">Pristupačni web</option>
        <option value="prskalice">Prskalice</option>
        <option value="poprskalice">Prepoznaj pojmove</option>
        <option value="kvizas">Kvizaš</option>
        <option value="poprskalice">Učimo slogove</option>
        <option value="ucimoRijeci">Učimo riječi</option>
        <option value="ucimoCitati">Učimo čtati</option>
        <option value="ucimoBoje">Učimo boje</option>
        <option value="ucimoMjere">Učimo mjere</option>
        <option value="ucimoPrijedloge">Učimo prijedloge</option>
        <option value="susretnica">Susretnica</option>
        <option value="vizualniRaspored">Vizualni raspored</option>
        <option value="vremenskiVrtuljak">Vremenski vrtuljak</option>
        <option value="zanimalica">Zanimalica</option>
        <option value="Ostalo">Ostalo</option>
      </select>
      {appName === "Ostalo" && ( 
        <div>
          <label>Unesite ime aplikacije: <span className="required">*</span></label>
          <input 
            type="text" 
            value={customAppName} 
            onChange={handleCustomAppNameChange} 
            required 
          />
        </div>
      )}
      <br /><br /> 
      <label>U kojem dijelu aplikacije ste uočili grešku: <span className="required">*</span></label>
      <select value={location} onChange={handleLocationChange} required>
        <option value="">Odaberite...</option>
        <option value="igri">U igri</option>
        <option value="postavkama">U postavkama</option>
        <option value="Ostalo">Ostalo</option>
      </select>
      {location === "Ostalo" && (
        <div>
          <label>U kojem dijelu aplikacje ste uočili grešku: <span className="required">*</span></label>
          <input
            type="text"
            value={customLocation}
            onChange={handleCustomLocationChange}
            required
          />
        </div>
      )}
      <br /><br /> 
      <label>Detaljno opišite prijavljenu grešku: <span className="required">*</span></label>
      <textarea 
        value={description} 
        onChange={handleDescriptionChange} 
        placeholder="Ovdje opišite pogrešku" 
        rows="4" 
        cols="50" 
        required
      />
      <br /><br /> 
      <label className="label-text">Ako ste u mogućnosti, uzmite screenshot greške ili uslikajte grešku ili snimite grešku, te uploadajte datoteku </label>
      <br /> 
      <label>(maksimalno 5 datoteka):</label>
      <input type="file" accept="image/*, video/*" onChange={handleMediaChange} multiple />
      <br />
      {media.length > 0 && (
        <div>
          <p>Uploadane datoteke:</p>
          <ul className='ime-datoteke'>
            {media.map((file, index) => (
              <li key={index}>
                {file.name} {}
                <button type="button" onClick={() => handleRemoveMedia(index)}>Ukloni</button> {}
              </li>
            ))}
          </ul>
        </div>
      )}
      <br /><br /> 
      <div className="title-container">
        <h1 className="form-title">Podaci o uređaju</h1>
        <h2 className="form-title-small">Unesite podatke o uređaju na kojem ste uočili grešku koju prijavljujete. Bez ovih podataka ponekad nismo u mogućnosti ispraviti greške.</h2>
      </div>
      <br /><br /> 
      <label>Odaberite vrstu uređaja: <span className="required">*</span></label>
      <div>
        <input 
          type="radio" 
          id="android" 
          name="deviceType" 
          value="Android" 
          checked={deviceType === "Android"} 
          onChange={handleDeviceTypeChange} 
          required 
        />
        <label htmlFor="android">Android</label>
      </div>
      <div>
        <input 
          type="radio" 
          id="ios" 
          name="deviceType" 
          value="iOS" 
          checked={deviceType === "iOS"} 
          onChange={handleDeviceTypeChange} 
          required 
        />
        <label htmlFor="ios">iOS</label>
      </div>
      <div>
      <input 
        type="radio" 
        id="web" 
        name="deviceType" 
        value="Web" 
        checked={deviceType === "Web"} 
        onChange={handleDeviceTypeChange} 
        required 
      />
      <label htmlFor="web">Web aplikacija</label>
      </div>
      {deviceType === "Web" && ( 
        <div>
          <label>Unesite vrstu internetskog preglednika: <span className="required">*</span></label>
          <input 
            type="text" 
            value={browserType} 
            onChange={handleBrowserTypeChange} 
            required 
          />
          <span className="example">Primjer unosa: Google Chrome, Mozilla Firefox, Safari,...</span>
        </div>
      )}
      <br /><br /> 
      <label>Unesite model uređaja: <span className="required">*</span></label>
      <input 
        type="text" 
        value={deviceModel} 
        onChange={handleDeviceModelChange} 
        required 
      />
      <span className="example">Primjer unosa: Samsung Galaxy S21, iPhone 13 Pro, Samsung Galaxy Tab S7, MacBook Pro 2021, Dell XPS 15,...</span>
      <br /><br /> 
      <label>Unesite verziju operacijskog sustava: <span className="required">*</span></label>
      <input 
        type="text" 
        value={osType} 
        onChange={handleOsTypeChange} 
        required 
      />
      <span className="example">Ovu informaciju pronađite na sljedeći način za Android uređaje: Postavke -&gt; O telefonu -&gt; Informacije o uređaju -&gt; Verzija sustava Android.</span>
      <span className="example">Za iOS uređaje postupak je sljedeći: Postavke -&gt; Općenito -&gt; Opis -&gt; Verzija softvera.</span>
      <span className="example">Za računala ili laptope postupak je sljedeći: Postavke -&gt; Sustav -&gt; O uređaju -&gt; Specifikacije uređaja.</span>
      <br />
      <span className="example">Primjer unosa: Android 11, iOS 15, Windows 10, Ubuntu 20.04 LTS,...</span>
      <br /><br />
      <label>Ukoliko želite da Vas dodatno kontaktiramo, upišite e-adresu:</label>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
      />
      <br /><br />
      <input type="submit" value="Pošalji" />
    </form>
  )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<MyForm />);
