import { useState } from 'react';
import ReactDOM from 'react-dom/client';
import "./App.css"; 
import logo from './logo_ict_aac.png';

function MyForm() {
  const [appName, setAppName] = useState("");
  const [location, setLocation] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('appName', appName);
    formData.append('location', location);
    formData.append('description', description);
    formData.append('image', image);
  }

  const handleAppChange = (event) => {
    setAppName(event.target.value);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="logo-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="form-title">Prijava grešaka na ICT-AAC aplikacijama</h1>
      </div>
      <br /><br /> 
      <label>Ime aplikacije u kojoj ste uočili grešku:</label>
      <select value={appName} onChange={handleAppChange} required>
        <option value="apl1">apl1</option>
        <option value="apl2">apl2</option>
        <option value="apl3">apl3</option>
      </select>
      <br /><br /> 
      <label>U kojem dijelu aplikacije ste uočili grešku:</label>
      <select value={location} onChange={handleLocationChange} required>
        <option value="location1">Location 1</option>
        <option value="location2">Location 2</option>
        <option value="location3">Location 3</option>
      </select>
      <br /><br /> 
      <label>Detaljno opišite prijavljenu grešku:</label>
      <textarea 
        value={description} 
        onChange={handleDescriptionChange} 
        placeholder="Ovdje opišite pogrešku" 
        rows="4" 
        cols="50" 
        required
      />
      <br /><br /> 
      <label>Uploadaj sliku:</label>
      <input type="file" onChange={handleImageChange} />
      <br /><br />
      <input type="submit" />
    </form>
  )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<MyForm />);
