import React, { useState, useEffect } from 'react';
import { xml2js } from 'xml-js';

function Login() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [formData, setFormData] = useState(null);

  const handleLogin = () => {
    // Ovdje provjerite korisničko ime i lozinku
    if (username === 'admin' && password === 'admin') {
      setLoggedIn(true);
      fetchFormData();
    } else {
      alert('Pogrešno korisničko ime ili lozinka!');
    }
  };

  const fetchFormData = () => {
    fetch('C:/Users/Mirta/Downloads/formData.xml')
      .then(response => response.text())
      .then(xml => {
        console.log('XML Data:', xml);
        const jsonData = xml2js(xml, { compact: true });
        console.log('JSON Data:', jsonData);
        setFormData(jsonData.form);
      })
      .catch(error => console.error('Error fetching XML file:', error));
  };

  if (!loggedIn) {
    return (
      <div>
        <h2>Prijava</h2>
        <input type="text" placeholder="Korisničko ime" value={username} onChange={(e) => setUsername(e.target.value)} />
        <input type="password" placeholder="Lozinka" value={password} onChange={(e) => setPassword(e.target.value)} />
        <button onClick={handleLogin}>Prijavi se</button>
      </div>
    );
  }

  if (!formData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Podaci iz XML datoteke:</h2>
      <table>
        <thead>
          <tr>
            <th>Polje</th>
            <th>Vrijednost</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(formData).map(([key, value]) => (
            <tr key={key}>
              <td>{key}</td>
              <td>{typeof value === 'object' ? JSON.stringify(value) : value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Login;
