import { useState } from 'react';
import ReactDOM from 'react-dom/client';
import "./App.css"; 
import logo from './logo_ict_aac.png';

function MyForm() {
  const [appName, setAppName] = useState("");
  const [location, setLocation] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The application name you selected was: ${appName}, and the location is: ${location}`);
    window.location.reload();
  }

  const handleAppChange = (event) => {
    setAppName(event.target.value);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="logo-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="form-title">Prijava grešaka na ICT-AAC aplikacijama</h1>
      </div>
      <br /><br /> 
      <label>Ime aplikacije u kojoj ste uočili pogrešku:</label>
      <select value={appName} onChange={handleAppChange}>
        <option value="apl1">apl1</option>
        <option value="apl2">apl2</option>
        <option value="apl3">apl3</option>
      </select>
      <br /><br /> 
      <label>Mjesto:</label>
      <select value={location} onChange={handleLocationChange}>
        <option value="location1">Location 1</option>
        <option value="location2">Location 2</option>
        <option value="location3">Location 3</option>
      </select>
      <br /><br /> 
      <input type="submit" />
    </form>
  )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<MyForm />);
