import React from 'react';
import { xml2js } from 'xml-js';
import { useState, useEffect } from 'react';

function DisplayFormData() {
  const [formData, setFormData] = useState([]);

  useEffect(() => {
    fetchData(); // Pozivamo funkciju za dohvaćanje podataka kad se komponenta mounta
  }, []); // Ova prazna lista ovisnosti osigurava da se useEffect izvršava samo jednom pri prvom renderiranju

  const fetchData = () => {
    fetch('http://localhost:5000/formData.xml') // Dohvaćamo XML datoteku s servera
      .then(response => response.text()) // Pretvaramo odgovor u tekst
      .then(xmlData => {
        const jsonData = xml2js(xmlData, { compact: true }); // Pretvaramo XML u JavaScript objekt
        const forms = jsonData.forms?.form || []; // Dobijamo niz formi iz JavaScript objekta
        setFormData(forms); // Postavljamo podatke u state
      })
      .catch(error => console.error('Greška pri dohvaćanju podataka:', error));
  };

  return (
    <div>
      <h2>Podaci iz XML datoteke:</h2>
      <table>
        <thead>
          <tr>
            <th>Vrijednost</th>
            <th>Ime aplikacije</th>
            <th>Ostalo ime aplikacije</th>
            <th>Mjesto aplikacije</th>
            <th>Ostalo mjesto aplikacije</th>
            <th>Opis problema</th>
            <th>Mediji</th>
            <th>Vrsta uređaja</th>
            <th>Vrsta internetskog preglednika</th>
            <th>Model uređaja</th>
            <th>Verzija operacijskog sustava</th>
            <th>E-adresa</th>
          </tr>
        </thead>
        <tbody>
          {/* Mapiramo podatke iz formData u redove tablice */}
          {formData.map((data, index) => (
            <tr key={index}>
              <td>{data.value}</td>
              <td>{data.appName}</td>
              {/* Dodajte ostale stupce podataka */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default DisplayFormData;
